txt = '' 
i = 0
# Part 1:
while i < len(txt):
    if len(set(txt[i : i + 4])) == 4:
        s = ["".join(letter) for letter in txt[i : i + 4]]
        y = "".join(s)
        print(y, txt.index(y) + 4)
        break
    i += 1

